import axios from 'axios';

const apiClient = axios.create({
  baseURL: "http://localhost:5248",
});

const apiUrl = "http://localhost:5248"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/todoitems
    `)    
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post(`${apiUrl}/todoitems`, {name:name,isComplete:false }) 
    return result.data;
  },


  setCompleted: async(id, IsComplete)=>{
    console.log('setCompleted', {id, IsComplete})
    const result = await axios.put(`${apiUrl}/todoitems/${id}`,{IsComplete:IsComplete}) 
    return result.data;
  },

  deleteTask:async(id)=>{
    const result = await axios.delete(`${apiUrl}/todoitems/${id}`) 
    return result.data
  }
};
